folder_path = 'images dairy'  # Folder to save the images


if not os.path.exists(folder_path):
    os.makedirs(folder_path)


for name, url in dairydata.items():
    
    file_name = os.path.join(folder_path, f'{name}.jpg')
    
    
    response = requests.get(url)
    
    
    if response.status_code == 200:
    
        with open(file_name, 'wb') as file:
            file.write(response.content)
        print(f'Successfully downloaded and saved {name}.jpg')
    else:
        print(f'Failed to download {name}.jpg')

print('All images downloaded.')

